-- ===================================================================
-- ESQUEMA COMPLETO PARA RADIO DIGITAL DE NOTICIAS ANIME
-- Base de datos: PostgreSQL
-- Normalizaci√≥n: 3FN
-- Fecha: 2025-10-11
-- ===================================================================

-- Extensiones √∫tiles
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
CREATE EXTENSION IF NOT EXISTS "pg_trgm"; -- Para b√∫squedas de texto eficientes

-- ===================================================================
-- 1. M√ìDULO DE USUARIOS Y ROLES
-- ===================================================================

-- Tabla de roles del sistema
CREATE TABLE roles (
    id SERIAL PRIMARY KEY,
    nombre VARCHAR(50) UNIQUE NOT NULL,
    descripcion TEXT,
    permisos JSONB DEFAULT '{}', -- Permisos espec√≠ficos en formato JSON
    activo BOOLEAN DEFAULT true,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Tabla de usuarios
CREATE TABLE usuarios (
    id SERIAL PRIMARY KEY,
    uuid UUID DEFAULT uuid_generate_v4() UNIQUE,
    nombre_usuario VARCHAR(100) UNIQUE NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL,
    password_hash VARCHAR(255) NOT NULL,
    rol_id INTEGER REFERENCES roles(id) ON DELETE SET NULL,
    
    -- Informaci√≥n del perfil
    nombre_completo VARCHAR(200),
    avatar TEXT, -- URL de la imagen
    titulo VARCHAR(150), -- "Editor Jefe", "Redactor Senior", etc.
    biografia TEXT,
    frase_personal TEXT,
    
    -- Redes sociales (JSON flexible)
    redes_sociales JSONB DEFAULT '{}',
    
    -- Especialidades y logros
    especialidades TEXT[], -- Array de especialidades anime
    logros TEXT[], -- Array de logros
    anime_favoritos TEXT[], -- Top anime del usuario
    
    -- M√©tricas del usuario
    nivel INTEGER DEFAULT 1,
    experiencia_a√±os INTEGER DEFAULT 0,
    articulos_publicados INTEGER DEFAULT 0,
    seguidores INTEGER DEFAULT 0,
    precision_rating INTEGER DEFAULT 85, -- Rating de precisi√≥n (0-100)
    
    -- Estados y control
    activo BOOLEAN DEFAULT true,
    verificado BOOLEAN DEFAULT false,
    ultima_conexion TIMESTAMP WITH TIME ZONE,
    fecha_registro TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    
    -- Auditor√≠a
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- ===================================================================
-- 2. M√ìDULO DE GESTI√ìN DE NOTICIAS
-- ===================================================================

-- Categor√≠as principales de noticias anime
CREATE TABLE categorias (
    id SERIAL PRIMARY KEY,
    nombre VARCHAR(100) UNIQUE NOT NULL,
    slug VARCHAR(120) UNIQUE NOT NULL,
    descripcion TEXT,
    color_hex VARCHAR(7) DEFAULT '#007bff', -- Color para UI
    icono VARCHAR(50), -- Nombre del icono (ej: lucide icons)
    orden_display INTEGER DEFAULT 0,
    activa BOOLEAN DEFAULT true,
    meta_title VARCHAR(200), -- SEO
    meta_description VARCHAR(500), -- SEO
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Etiquetas/Tags para noticias
CREATE TABLE etiquetas (
    id SERIAL PRIMARY KEY,
    nombre VARCHAR(80) UNIQUE NOT NULL,
    slug VARCHAR(100) UNIQUE NOT NULL,
    descripcion TEXT,
    uso_count INTEGER DEFAULT 0, -- Contador de uso
    trending BOOLEAN DEFAULT false, -- Tag en tendencia
    color_hex VARCHAR(7) DEFAULT '#6c757d',
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Estados de las noticias
CREATE TABLE estados_noticia (
    id SERIAL PRIMARY KEY,
    nombre VARCHAR(50) UNIQUE NOT NULL, -- borrador, revision, publicada, archivada
    descripcion TEXT,
    color_hex VARCHAR(7) DEFAULT '#28a745',
    activo BOOLEAN DEFAULT true
);

-- Tabla principal de noticias
CREATE TABLE noticias (
    id SERIAL PRIMARY KEY,
    uuid UUID DEFAULT uuid_generate_v4() UNIQUE,
    
    -- Contenido principal
    titulo VARCHAR(300) NOT NULL,
    slug VARCHAR(350) UNIQUE NOT NULL,
    resumen TEXT,
    contenido TEXT NOT NULL,
    
    -- Multimedia
    imagen_principal TEXT, -- URL imagen destacada
    galeria_imagenes JSONB DEFAULT '[]', -- Array de URLs de im√°genes
    audio_url TEXT, -- URL del audio/podcast relacionado
    video_url TEXT, -- URL del video relacionado
    
    -- Organizaci√≥n
    categoria_id INTEGER REFERENCES categorias(id) ON DELETE SET NULL,
    autor_id INTEGER REFERENCES usuarios(id) ON DELETE SET NULL,
    estado_id INTEGER DEFAULT 1 REFERENCES estados_noticia(id),
    
    -- Programaci√≥n y fechas
    fecha_publicacion DATE NOT NULL DEFAULT CURRENT_DATE,
    fecha_programada TIMESTAMP WITH TIME ZONE, -- Para programar publicaci√≥n
    
    -- M√©tricas y engagement
    visitas INTEGER DEFAULT 0,
    likes INTEGER DEFAULT 0,
    shares INTEGER DEFAULT 0,
    tiempo_lectura INTEGER DEFAULT 0, -- En minutos
    
    -- Configuraci√≥n
    destacada BOOLEAN DEFAULT false,
    permite_comentarios BOOLEAN DEFAULT true,
    comentarios_moderados BOOLEAN DEFAULT true,
    
    -- SEO y metadatos
    meta_title VARCHAR(200),
    meta_description VARCHAR(500),
    meta_keywords TEXT,
    
    -- Contenido estructurado
    datos_estructurados JSONB DEFAULT '{}', -- Schema.org JSON-LD
    
    -- Auditor√≠a
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    deleted_at TIMESTAMP WITH TIME ZONE -- Soft delete
);

-- Relaci√≥n many-to-many entre noticias y etiquetas
CREATE TABLE noticia_etiquetas (
    noticia_id INTEGER REFERENCES noticias(id) ON DELETE CASCADE,
    etiqueta_id INTEGER REFERENCES etiquetas(id) ON DELETE CASCADE,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (noticia_id, etiqueta_id)
);

-- ===================================================================
-- 3. M√ìDULO DE COMENTARIOS Y ENGAGEMENT
-- ===================================================================

-- Comentarios de las noticias
CREATE TABLE comentarios (
    id SERIAL PRIMARY KEY,
    uuid UUID DEFAULT uuid_generate_v4() UNIQUE,
    noticia_id INTEGER REFERENCES noticias(id) ON DELETE CASCADE,
    padre_id INTEGER REFERENCES comentarios(id) ON DELETE CASCADE, -- Para respuestas anidadas
    
    -- Autor del comentario
    usuario_id INTEGER REFERENCES usuarios(id) ON DELETE SET NULL, -- NULL si es an√≥nimo
    autor_nombre VARCHAR(100), -- Para comentarios an√≥nimos
    autor_email VARCHAR(255), -- Para comentarios an√≥nimos
    autor_avatar TEXT,
    
    -- Contenido
    contenido TEXT NOT NULL,
    tiene_spoilers BOOLEAN DEFAULT false,
    
    -- Estado y moderaci√≥n
    aprobado BOOLEAN DEFAULT false,
    spam BOOLEAN DEFAULT false,
    reportado BOOLEAN DEFAULT false,
    
    -- M√©tricas
    likes INTEGER DEFAULT 0,
    respuestas_count INTEGER DEFAULT 0,
    
    -- Metadatos
    ip_address INET,
    user_agent TEXT,
    
    -- Auditor√≠a
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    moderado_por INTEGER REFERENCES usuarios(id) ON DELETE SET NULL,
    fecha_moderacion TIMESTAMP WITH TIME ZONE
);

-- ===================================================================
-- 4. M√ìDULO DE M√âTRICAS Y ANALYTICS
-- ===================================================================

-- M√©tricas diarias de noticias
CREATE TABLE metricas_noticias (
    id SERIAL PRIMARY KEY,
    noticia_id INTEGER REFERENCES noticias(id) ON DELETE CASCADE,
    fecha DATE NOT NULL,
    visitas INTEGER DEFAULT 0,
    visitas_unicas INTEGER DEFAULT 0,
    tiempo_promedio_lectura INTEGER DEFAULT 0, -- segundos
    porcentaje_completitud DECIMAL(5,2) DEFAULT 0.00, -- % de art√≠culo le√≠do
    likes INTEGER DEFAULT 0,
    shares INTEGER DEFAULT 0,
    comentarios INTEGER DEFAULT 0,
    origen_trafico JSONB DEFAULT '{}', -- Google, directo, redes sociales, etc.
    dispositivos JSONB DEFAULT '{}', -- mobile, desktop, tablet
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    UNIQUE(noticia_id, fecha)
);

-- M√©tricas globales del sitio
CREATE TABLE metricas_sitio (
    id SERIAL PRIMARY KEY,
    fecha DATE NOT NULL UNIQUE,
    visitas_totales INTEGER DEFAULT 0,
    usuarios_activos INTEGER DEFAULT 0,
    paginas_vistas INTEGER DEFAULT 0,
    sesiones INTEGER DEFAULT 0,
    duracion_promedio_sesion INTEGER DEFAULT 0, -- segundos
    tasa_rebote DECIMAL(5,2) DEFAULT 0.00,
    noticias_publicadas INTEGER DEFAULT 0,
    comentarios_nuevos INTEGER DEFAULT 0,
    registros_usuarios INTEGER DEFAULT 0,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- ===================================================================
-- 5. M√ìDULO DE AUDITOR√çA Y LOGS
-- ===================================================================

-- Registro de acciones del sistema
CREATE TABLE logs_actividad (
    id SERIAL PRIMARY KEY,
    usuario_id INTEGER REFERENCES usuarios(id) ON DELETE SET NULL,
    accion VARCHAR(100) NOT NULL, -- crear, editar, eliminar, publicar, etc.
    entidad VARCHAR(100) NOT NULL, -- noticia, usuario, comentario, etc.
    entidad_id INTEGER,
    descripcion TEXT,
    datos_anteriores JSONB, -- Estado antes del cambio
    datos_nuevos JSONB, -- Estado despu√©s del cambio
    ip_address INET,
    user_agent TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- ===================================================================
-- 6. M√ìDULO DE PROGRAMAS Y PODCASTS
-- ===================================================================

-- Programas de radio/podcasts
CREATE TABLE programas (
    id SERIAL PRIMARY KEY,
    uuid UUID DEFAULT uuid_generate_v4() UNIQUE,
    nombre VARCHAR(200) NOT NULL,
    slug VARCHAR(250) UNIQUE NOT NULL,
    descripcion TEXT,
    imagen_portada TEXT,
    conductor_principal_id INTEGER REFERENCES usuarios(id) ON DELETE SET NULL,
    
    -- Horarios y frecuencia
    dia_semana INTEGER[], -- Array: 1=Lunes, 7=Domingo
    hora_inicio TIME,
    duracion_minutos INTEGER DEFAULT 60,
    zona_horaria VARCHAR(50) DEFAULT 'America/Lima',
    
    -- Estado
    activo BOOLEAN DEFAULT true,
    destacado BOOLEAN DEFAULT false,
    
    -- M√©tricas
    suscriptores INTEGER DEFAULT 0,
    episodios_count INTEGER DEFAULT 0,
    
    -- Metadatos
    generos TEXT[], -- Tipos de contenido anime
    audiencia_objetivo VARCHAR(100),
    
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Episodios de programas
CREATE TABLE episodios (
    id SERIAL PRIMARY KEY,
    uuid UUID DEFAULT uuid_generate_v4() UNIQUE,
    programa_id INTEGER REFERENCES programas(id) ON DELETE CASCADE,
    
    -- Contenido
    titulo VARCHAR(300) NOT NULL,
    descripcion TEXT,
    numero_episodio INTEGER,
    temporada INTEGER DEFAULT 1,
    
    -- Audio/Video
    audio_url TEXT NOT NULL,
    duracion_segundos INTEGER,
    tama√±o_archivo_mb DECIMAL(10,2),
    transcripcion TEXT,
    
    -- Programaci√≥n
    fecha_emision TIMESTAMP WITH TIME ZONE,
    fecha_publicacion TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    
    -- Estado
    publicado BOOLEAN DEFAULT false,
    destacado BOOLEAN DEFAULT false,
    
    -- M√©tricas
    reproducciones INTEGER DEFAULT 0,
    descargas INTEGER DEFAULT 0,
    likes INTEGER DEFAULT 0,
    
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- ===================================================================
-- 7. M√ìDULO DE PUBLICIDAD Y BANNERS
-- ===================================================================

-- Banners publicitarios
CREATE TABLE banners (
    id SERIAL PRIMARY KEY,
    nombre VARCHAR(200) NOT NULL,
    tipo VARCHAR(50) NOT NULL, -- header, sidebar, footer, popup, inline
    imagen_url TEXT,
    link_destino TEXT,
    alt_text VARCHAR(255),
    
    -- Programaci√≥n
    fecha_inicio DATE,
    fecha_fin DATE,
    activo BOOLEAN DEFAULT true,
    
    -- Targeting
    categorias_objetivo INTEGER[], -- IDs de categor√≠as donde mostrar
    dispositivos_objetivo VARCHAR(20)[], -- mobile, desktop, tablet
    
    -- M√©tricas
    impresiones INTEGER DEFAULT 0,
    clicks INTEGER DEFAULT 0,
    
    -- Configuraci√≥n
    peso_prioridad INTEGER DEFAULT 1, -- Para ordenar banners
    max_impresiones_diarias INTEGER,
    
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- ===================================================================
-- 8. M√ìDULO DE SUSCRIPCIONES Y NOTIFICACIONES
-- ===================================================================

-- Suscripciones a newsletters/notificaciones
CREATE TABLE suscripciones (
    id SERIAL PRIMARY KEY,
    email VARCHAR(255) UNIQUE NOT NULL,
    usuario_id INTEGER REFERENCES usuarios(id) ON DELETE CASCADE, -- NULL si no est√° registrado
    nombre VARCHAR(200),
    
    -- Preferencias
    notificar_noticias_nuevas BOOLEAN DEFAULT true,
    notificar_episodios_nuevos BOOLEAN DEFAULT true,
    notificar_comentarios_respuesta BOOLEAN DEFAULT true,
    categorias_interes INTEGER[], -- IDs de categor√≠as de inter√©s
    
    -- Estado
    activa BOOLEAN DEFAULT true,
    verificada BOOLEAN DEFAULT false,
    token_verificacion UUID DEFAULT uuid_generate_v4(),
    
    -- Frecuencia
    frecuencia VARCHAR(20) DEFAULT 'semanal', -- inmediata, diaria, semanal
    
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Cola de notificaciones
CREATE TABLE notificaciones (
    id SERIAL PRIMARY KEY,
    usuario_id INTEGER REFERENCES usuarios(id) ON DELETE CASCADE,
    tipo VARCHAR(50) NOT NULL, -- nueva_noticia, nuevo_episodio, comentario, etc.
    titulo VARCHAR(200) NOT NULL,
    mensaje TEXT NOT NULL,
    link_destino TEXT,
    
    -- Estado
    leida BOOLEAN DEFAULT false,
    enviada BOOLEAN DEFAULT false,
    
    -- Metadatos
    datos_extra JSONB DEFAULT '{}',
    prioridad INTEGER DEFAULT 1, -- 1=normal, 2=alta, 3=urgente
    
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    fecha_envio TIMESTAMP WITH TIME ZONE,
    fecha_lectura TIMESTAMP WITH TIME ZONE
);

-- ===================================================================
-- 9. √çNDICES PARA OPTIMIZACI√ìN
-- ===================================================================

-- √çndices para b√∫squedas de texto
CREATE INDEX idx_noticias_titulo_busqueda ON noticias USING gin(to_tsvector('spanish', titulo));
CREATE INDEX idx_noticias_contenido_busqueda ON noticias USING gin(to_tsvector('spanish', contenido));
CREATE INDEX idx_noticias_slug ON noticias(slug);
CREATE INDEX idx_noticias_fecha_publicacion ON noticias(fecha_publicacion DESC);
CREATE INDEX idx_noticias_destacadas ON noticias(destacada) WHERE destacada = true;
CREATE INDEX idx_noticias_categoria ON noticias(categoria_id);
CREATE INDEX idx_noticias_autor ON noticias(autor_id);
CREATE INDEX idx_noticias_estado ON noticias(estado_id);

-- √çndices para usuarios
CREATE INDEX idx_usuarios_email ON usuarios(email);
CREATE INDEX idx_usuarios_nombre_usuario ON usuarios(nombre_usuario);
CREATE INDEX idx_usuarios_activos ON usuarios(activo) WHERE activo = true;
CREATE INDEX idx_usuarios_rol ON usuarios(rol_id);

-- √çndices para comentarios
CREATE INDEX idx_comentarios_noticia ON comentarios(noticia_id);
CREATE INDEX idx_comentarios_aprobados ON comentarios(aprobado) WHERE aprobado = true;
CREATE INDEX idx_comentarios_padre ON comentarios(padre_id);
CREATE INDEX idx_comentarios_fecha ON comentarios(created_at DESC);

-- √çndices para m√©tricas
CREATE INDEX idx_metricas_noticias_fecha ON metricas_noticias(fecha DESC);
CREATE INDEX idx_metricas_sitio_fecha ON metricas_sitio(fecha DESC);

-- √çndices para logs
CREATE INDEX idx_logs_usuario ON logs_actividad(usuario_id);
CREATE INDEX idx_logs_fecha ON logs_actividad(created_at DESC);
CREATE INDEX idx_logs_entidad ON logs_actividad(entidad, entidad_id);

-- ===================================================================
-- 10. TRIGGERS PARA AUTOMATIZACI√ìN
-- ===================================================================

-- Funci√≥n para actualizar updated_at autom√°ticamente
CREATE OR REPLACE FUNCTION actualizar_updated_at()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$$ language 'plpgsql';

-- Aplicar trigger a todas las tablas que tengan updated_at
CREATE TRIGGER trigger_usuarios_updated_at BEFORE UPDATE ON usuarios FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_noticias_updated_at BEFORE UPDATE ON noticias FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_categorias_updated_at BEFORE UPDATE ON categorias FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_comentarios_updated_at BEFORE UPDATE ON comentarios FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_programas_updated_at BEFORE UPDATE ON programas FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_episodios_updated_at BEFORE UPDATE ON episodios FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_banners_updated_at BEFORE UPDATE ON banners FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();
CREATE TRIGGER trigger_suscripciones_updated_at BEFORE UPDATE ON suscripciones FOR EACH ROW EXECUTE FUNCTION actualizar_updated_at();

-- Trigger para actualizar contador de etiquetas
CREATE OR REPLACE FUNCTION actualizar_contador_etiquetas()
RETURNS TRIGGER AS $$
BEGIN
    IF TG_OP = 'INSERT' THEN
        UPDATE etiquetas SET uso_count = uso_count + 1 WHERE id = NEW.etiqueta_id;
        RETURN NEW;
    ELSIF TG_OP = 'DELETE' THEN
        UPDATE etiquetas SET uso_count = GREATEST(uso_count - 1, 0) WHERE id = OLD.etiqueta_id;
        RETURN OLD;
    END IF;
    RETURN NULL;
END;
$$ language 'plpgsql';

CREATE TRIGGER trigger_contador_etiquetas 
    AFTER INSERT OR DELETE ON noticia_etiquetas 
    FOR EACH ROW EXECUTE FUNCTION actualizar_contador_etiquetas();

-- Trigger para actualizar contador de respuestas en comentarios
CREATE OR REPLACE FUNCTION actualizar_contador_respuestas()
RETURNS TRIGGER AS $$
BEGIN
    IF TG_OP = 'INSERT' AND NEW.padre_id IS NOT NULL THEN
        UPDATE comentarios SET respuestas_count = respuestas_count + 1 WHERE id = NEW.padre_id;
        RETURN NEW;
    ELSIF TG_OP = 'DELETE' AND OLD.padre_id IS NOT NULL THEN
        UPDATE comentarios SET respuestas_count = GREATEST(respuestas_count - 1, 0) WHERE id = OLD.padre_id;
        RETURN OLD;
    END IF;
    RETURN NULL;
END;
$$ language 'plpgsql';

CREATE TRIGGER trigger_contador_respuestas 
    AFTER INSERT OR DELETE ON comentarios 
    FOR EACH ROW EXECUTE FUNCTION actualizar_contador_respuestas();

-- Crear tabla de historial de noticias para auditor√≠a
CREATE TABLE IF NOT EXISTS noticia_historial (
    id SERIAL PRIMARY KEY,
    noticia_id INTEGER NOT NULL REFERENCES noticias(id) ON DELETE CASCADE,
    usuario_id INTEGER REFERENCES usuarios(id),
    accion VARCHAR(50) NOT NULL,
    cambios JSONB,
    comentario TEXT,
    created_at TIMESTAMPTZ DEFAULT now()
);


